import ListContainer from "../../components/ListContainer";
import { useEffect, useState } from "react";
function TP3({ item }) {
  return (
    <>
      <div>
      {item.model} ({item.brand}) année: {item.years} km: {item.kms} prix: {item.price}€ -
    </div>
    </>
  );
}

function CartForm({ onSubmit }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/cars")
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      });
  }, []);

  const [itemId, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    if (!itemId && products.length) {
      setSelectedProduct(products[0].id);
    }
  }, [products]);

  function handleSubmit(e) {
    e.preventDefault();
    // Récupération des valeurs du formulaire en JS natif
    //const formData = new FormData(e.target);
    //const itemId = parseInt(formData.get("product"));
    //const quantity = parseInt(formData.get("quantity"));
    onSubmit({ product: itemId, quantity });
  }

  return (
    <form onSubmit={handleSubmit}>
      <select
        name="product"
        onChange={(e) => setSelectedProduct(parseInt(e.target.value))}
      >
        {products.map((product) => (
          <option key={product.id} value={product.id}>
            {product.name}
          </option>
        ))}
      </select>
      <input
        type="string"
        name="quantity"
        onChange={(e) => setQuantity(parseInt(e.target.value))}
      />
      <input type="submit" value="Add to Cart" />
    </form>
  );
}


export default function Cart() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/cars")
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
      });
  }, []);

  return (
    <>
      <h1>Nos véhicules !</h1>
      <ListContainer
        initialItems={products}
        ListItem={TP3}
        availableActions={{}}
      />
      <CartForm/>
    </>
  );
}
